import { useHistory, useParams } from 'react-router-dom';

import logoImg from '../assets/images/logo.svg';
import deleteImg from '../assets/images/delete.svg';
import chekImg from '../assets/images/check.svg';
import { Button } from '../components/Button';
import { Question } from '../components/Question';
import { RoomCode } from '../components/RoomCode';
//import { useAuth } from '../hooks/useAuth';
import { useRoom } from '../hooks/useRoom';
import { database } from '../services/firebase';

import '../styles/room.scss';
import { async } from 'q';

type RoomParams = {
    id: string;
}

export function AdminRoom(){
    //const { user } = useAuth(); // apenas usuario autenticado poderá fazer novas perguntas
    const history = useHistory()
    const params = useParams<RoomParams>(); //parametros que essa rota vai receber
    const roomId = params.id;
    const { title, questions } = useRoom(roomId)
    
    async function handleEndRoom(){
      await  database.ref(`rooms/${roomId}`).update({
          endedAt: new Date(), // falando que essa sala foi encerrada
        })
        history.push('/');
    }
   async function handleDeleteQuestion(questionId: string){
        if(window.confirm('Tem certeza que você deseja excluir esta pergunta?')){
            const questionRef = await database.ref(`rooms/${roomId}/questions/${questionId}`).remove();
        }
    }
     return ( //componente
        <div id="page-room">
            <header>
                <div className="content">
                    <img src={logoImg} alt="Letmeask" />   
                    <div>
                        <RoomCode code={params.id} /> 
                        <Button isOutlined onClick={handleEndRoom}>Encerrar sala</Button>  
                    </div>          
                </div>
            </header>

            <main>
                <div className="room-title">
                    <h1>Sala {title}</h1>
                   { questions.length > 0 && <span>{questions.length} pergunta(s)</span>}
                </div>

                <div className="question-list">
                    {questions.map(question => {
                        return (
                            <Question // element
                                key={question.id}
                                content={question.content}
                                author={question.author}
                            >
                                <button
                                    type="button"
                                    onClick={()=> handleDeleteQuestion(question.id)}
                                >
                                    <img src={deleteImg} alt="Remover pergunta" />
                                </button>
                            </Question>
                        );      
                    })} 
                </div>
            </main>
        </div>
    );
}
