import { FormEvent, useState } from 'react';
import { useParams } from 'react-router-dom';

import logoImg from '../assets/images/logo.svg';
import { Button } from '../components/Button';
import { Question } from '../components/Question';
import { RoomCode } from '../components/RoomCode';
import { useAuth } from '../hooks/useAuth';
import { database } from '../services/firebase';

import '../styles/room.scss';


type RoomParams = {
    id: string;
}

export function Room(){

    const { user } = useAuth(); // apenas usuario autenticado poderá fazer novas perguntas
    const params = useParams<RoomParams>(); //parametros que essa rota vai receber
    const [newQuestion, setNewQuestion] = useState(''); // informacao da nova pergunta
    const roomId = params.id;
    const { title, questions} = useRoom(roomId)
    

    async function handleSendQuestion(event: FormEvent){
        event.preventDefault();
        if(newQuestion.trim() == ''){
            return;
        }
        
        if (!user){
            throw new Error('You  must be logged in');

        }
        const question = {
            content: newQuestion,
            author: {
                name: user?.name,
                avatar: user.avatar,
               
            },
            isHighligted: false, 
            inAnwsered: false
        };

        await database.ref(`rooms/${roomId}/questions`).push(question); 

        setNewQuestion(''); 
    }
    return ( //componente
        <div id="page-room">
            <header>
                <div className="content">
                    <img src={logoImg} alt="Letmeask" />   
                    <RoomCode code={params.id} />              
                </div>
            </header>

            <main>
                <div className="room-title">
                    <h1>Sala {title}</h1>
                   { questions.length > 0 && <span>{questions.length} pergunta(s)</span>}
                </div>

                <form onSubmit={handleSendQuestion}>
                    <textarea 
                         placeholder="O que você quer perguntar?"
                         onChange={event => setNewQuestion(event.target.value)} // pegar o valor do input conforme é digitado
                         value={newQuestion} // caso altere o valor
                    />
                    <div className="form-footer">
                       { user ? (
                           <div className="user-info"> 
                               <img src={user.avatar} alt={user.name}/>
                               <span>{user.name}</span>
                            </div>
                        ) : (
                        <span>Para enviar uma pergunta, <button>faça seu login</button>.</span>
                       ) }
                        <Button type="submit" disabled={!user}>Enviar pergunta</Button>
                    </div>
                </form>
                <div className="question-list">
                    {questions.map(question => {
                        return (
                            <Question // element
                                key={question.id}
                                content={question.content}
                                author={question.author}
                            />
                        );      
                    })} 
                </div>
            </main>
        </div>
    );
}
