import { ReactNode} from 'react'; // definir propriedade childreen
import './styles.scss';


type QuestionProps = { // quais propriedades este componente recebe
    content: string;
    author: {
        name: string;
        avatar: string;
    };
    children?: ReactNode; // qualquer coisa aceitavel dentro de return (texto, div, componente etc)
}



export function Question({
    content,
    author,
 }: QuestionProps){
    return (
        <div className="question">
            <p>{content}</p>
            <footer>
                <div className="user-info">
                    <img src={author.avatar} alt={author.name} />
                    <span>{author.name}</span>
                </div>
                <div></div>
            </footer> 
        </div>        
    );
}
