import { ReactNode} from 'react'; // definir propriedade childreen
import './styles.scss';


type QuestionProps = { // quais propriedades este componente recebe
    content: string;
    author: {
        name: string;
        avatar: string;
    };
    children?: ReactNode; // qualquer coisa aceitavel dentro de return (texto, div, componente etc)
    isAnswered: boolean;
    isHighlighted: boolean;
}

export function Question({
    content,
    author,
    isAnswered = false,
    isHighlighted = false,
    children,
 }: QuestionProps){
    return (
        <div 
          className={`question ${isAnswered ? 'answered': ''} ${isHighlighted ? 'isHighlighted' : ''}`}>
            <p>{content}</p>
            <footer>
                <div className="user-info">
                    <img src={author.avatar} alt={author.name} />
                    <span>{author.name}</span>
                </div>
                <div>
                   {children} 
                </div>
            </footer> 
        </div>        
    );
}
